import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  render() {
    return (
      <div>
        <button
          onClick={this.props.onReset()}
          className="btn btn-primary btn-sm m-2"
        >
          Reset
        </button>
        {this.state.counters.map(counter => (
          <Counter
            key={counter.id}
            onDelete={this.props.onDelete(this.props.counter.id)}
            onIncrement={this.props.onIncrement(this.props.counter)}
            onDecrement={this.props.onDecrement(this.props.counter)}
            counter={counter}
          ></Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
